CREATE TABLE ROLES (
  ID_ROL SERIAL PRIMARY KEY NOT NULL,
  NOMBRE_ROL VARCHAR(50) NOT NULL,
  DESCRIPCION_ROL VARCHAR(100) NOT NULL
);

CREATE TABLE USUARIOS (
  ID_USUARIO SERIAL PRIMARY KEY NOT NULL,
  NOMBRE_USUARIO VARCHAR(50) NOT NULL,
  APELLIDO_USUARIO VARCHAR(50) NOT NULL,
  USUARIO_USUARIO VARCHAR(50) NOT NULL UNIQUE,
  CONTRASENA_USUARIO VARCHAR(50) NOT NULL,
  CORREO_USUARIO VARCHAR(50) NOT NULL UNIQUE,
  FECHA_REGISTRO_USUARIO DATE NOT NULL DEFAULT CURRENT_DATE,
  ESTADO_USUARIO BOOLEAN NOT NULL DEFAULT TRUE,
  FK_ROL INT NOT NULL,
  FOREIGN KEY (FK_ROL) REFERENCES ROLES(ID_ROL) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE RUBROS (
  ID_RUBRO SERIAL PRIMARY KEY NOT NULL,
  CODIGO_RUBRO VARCHAR(10) NOT NULL,
  NOMBRE_RUBRO VARCHAR(50) NOT NULL,
  DESCRIPCION_RUBRO VARCHAR(100) NOT NULL
);

CREATE TABLE CUENTASMAYORES (
  ID_CUENTASMAYORES SERIAL NOT NULL PRIMARY KEY,
  CODIGO_CUENTAS_MAYORES  VARCHAR(10) NOT NULL,
  NOMBRE_CUENTAS_MAYORES VARCHAR(50) NOT NULL,
  DESCRIPCION_CUENTAS_MAYORES VARCHAR(100) NOT NULL,
  FK_RUBRO INT NOT NULL,
  FOREIGN KEY (FK_RUBRO) REFERENCES RUBROS(ID_RUBRO) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE CUENTAS(
 ID_CUENTAS SERIAL NOT NULL PRIMARY KEY,
 CODIGO_CUENTAS VARCHAR(10) NOT NULL,
 NOMBRE_CUENTAS VARCHAR(50) NOT NULL,
 DESCRIPCION_CUENTAS VARCHAR(100) NOT NULL,
 FK_CUENTASMAYORES INT NOT NULL,
 FOREIGN KEY (FK_CUENTASMAYORES) REFERENCES CUENTASMAYORES(ID_CUENTASMAYORES) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE SUBCUENTAS (
  ID_SUBCUENTAS SERIAL NOT NULL PRIMARY KEY,
  CODIGO_SUBCUENTAS VARCHAR(10) NOT NULL,
  NOMBRE_SUBCUENTAS VARCHAR(50) NOT NULL,
  DESCRIPCION_SUBCUENTAS VARCHAR(100) NOT NULL,
  FK_CUENTAS INT NOT NULL,
  FOREIGN KEY (FK_CUENTAS) REFERENCES CUENTAS(ID_CUENTAS) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE PARTIDAS(
 ID_PARTIDA SERIAL NOT NULL PRIMARY KEY,
 DESCRIPCION_PARTIDA VARCHAR(100) NOT NULL,
 FECHA_PARTIDA DATE NOT NULL
);

CREATE TABLE DETALLES_PARTIDAS(
  ID_DETALLE_PARTIDA SERIAL NOT NULL PRIMARY KEY,
  MONTO DECIMAL(10,2) NOT NULL,
  TIPO VARCHAR(10) NOT NULL DEFAULT 'DEBE',
  FK_PARTIDA INT NOT NULL,
  FK_SUBCUENTAS INT NOT NULL,
  FOREIGN KEY (FK_PARTIDA) REFERENCES PARTIDAS(ID_PARTIDA) ON DELETE CASCADE ON UPDATE CASCADE,
  FOREIGN KEY (FK_SUBCUENTAS) REFERENCES SUBCUENTAS(ID_SUBCUENTAS) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT CHK_TIPO CHECK (TIPO IN ('DEBE','HABER'))
);


































































































